document.addEventListener('DOMContentLoaded', function () {
  const shareBtn = document.getElementById('post-share-btn');
  const modalOverlay = document.getElementById('share-modal-overlay');
  if (!shareBtn || !modalOverlay) return;
  const postUrl = window.location.href;
  const postTitle = document.querySelector('.post-title')?.innerText || 'Check out this post';
  shareBtn.addEventListener('click', function () {
    modalOverlay.style.display = 'block';
  });
  const closeModal = () => {
    modalOverlay.style.display = 'none';
  };
  modalOverlay.addEventListener('click', function (e) {
    if (e.target === modalOverlay) closeModal();
  });
  document.addEventListener('keydown', function (e) {
    if (e.key === 'Escape') closeModal();
  });
  document.getElementById('close-modal-btn')?.addEventListener('click', closeModal);
  window.shareTo = function(platform) {
    const encodedUrl = encodeURIComponent(postUrl);
    const encodedTitle = encodeURIComponent(postTitle);
    let shareUrl = '';
    switch (platform) {
      case 'whatsapp':
        shareUrl = `https://wa.me/?text= ${encodedTitle} ${encodedUrl}`;
        break;
      case 'facebook':
        shareUrl = `https://www.facebook.com/sharer.php?u= ${encodedUrl}&t=${encodedTitle}`;
        break;
      case 'twitter':
        shareUrl = `https://twitter.com/intent/tweet?url= ${encodedUrl}&text=${encodedTitle}`;
        break;
      case 'telegram':
        shareUrl = `https://t.me/share/url?url= ${encodedUrl}&text=${encodedTitle}`;
        break;
      case 'email':
        shareUrl = `mailto:?subject=${encodedTitle}&body=Check out this post: ${encodedUrl}`;
        break;
    }
    if (shareUrl) window.open(shareUrl, '_blank');
    closeModal();
  };
  window.copyLink = function () {
    navigator.clipboard.writeText(postUrl).then(() => {
      alert('Link copied to clipboard!');
    }).catch(err => {
      alert('Failed to copy link.');
      console.error('Copy failed:', err);
    });
    closeModal();
  };
});
