document.addEventListener('DOMContentLoaded', function () {
  const postList = document.getElementById('post-list');
  const posts = Array.from(postList?.getElementsByClassName('post-card') || []);
  const loadMoreBtn = document.getElementById('load-more-btn');
  const loader = document.querySelector('.load-more-loader');
  const btnText = document.querySelector('.btn-text');

  let currentIndex = 6;

  // Initially hide extra posts
  if (posts.length > 0) {
    posts.forEach((post, index) => {
      if (index >= currentIndex) {
        post.style.display = 'none';
      } else {
        post.classList.add('visible');
      }
    });
  }

  // Show loader only
  function showLoader() {
    loadMoreBtn.classList.add('loading');
    loader.style.display = 'inline-block';
    btnText.textContent = '';
    loadMoreBtn.disabled = true;
  }

  // Hide loader and restore "Load More"
  function hideLoaderAndCheck() {
    loadMoreBtn.classList.remove('loading');
    loader.style.display = 'none';
    btnText.textContent = 'Load More';
    loadMoreBtn.disabled = false;
  }

  // Load More Button Click
  if (loadMoreBtn && posts.length > 6) {
    loadMoreBtn.addEventListener('click', function () {
      if (currentIndex >= posts.length) return;

      showLoader();

      setTimeout(() => {
        const nextPosts = posts.slice(currentIndex, currentIndex + 6);

        nextPosts.forEach(post => {
          post.style.display = 'flex';
          post.classList.add('visible');
        });

        currentIndex += 6;
        hideLoaderAndCheck();

        if (currentIndex >= posts.length) {
          btnText.textContent = 'That\'s All!';
          loader.style.display = 'none';
          loadMoreBtn.disabled = true;
          loadMoreBtn.classList.add('done');
        }
      }, 1000); // 1 sec delay
    });
  }

  // If already at limit
  if (posts.length <= 6) {
    btnText.textContent = 'That\'s All!';
    loader.style.display = 'none';
    loadMoreBtn.disabled = true;
    loadMoreBtn.classList.add('done');
  }
});
