function shuffleArray(array) {
  for (let i = array.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [array[i], array[j]] = [array[j], array[i]];
  }
}

function renderRelatedPosts(container, data) {
  const blogName = data.feed.title.$t || 'Unknown Blog';
  const currentPostUrl = window.location.href.split('?')[0];
  let posts = [];

  data.feed.entry.forEach(entry => {
    const title = entry.title.$t;
    const link = entry.link.find(l => l.rel === 'alternate').href;

    if (link === currentPostUrl) return;

    let image = 'https://blogger.googleusercontent.com/img/b/R29vZ2xl/AVvXsEiYONw4JEOOZD7-wy7_5BvaDea9rJSaN8WgBcR2AvSpw3A-JLYNIIu0C4rLx23_uybDT-WfvFlILBbSFiGi6k_ssgUy2VN6HcP_UcmkiWk5e8uF-RIVysM_TlKnVwGC_OvwTzwR6WiURT-1SUvCkLx-CRIXJe1U3s2cLadHOnu-r0yTE_AcfHSDdnzIGhwW/s988/1000046130.png ';
    if (entry.media$thumbnail) {
      image = entry.media$thumbnail.url.replace(/\/s72-c/, '/s320');
    } else if (entry.content.$t && /<img.*?src=["'](.*?)["']/i.test(entry.content.$t)) {
      image = entry.content.$t.match(/<img.*?src=["'](.*?)["']/i)[1];
    }

    posts.push({ title, link, image });
  });

  shuffleArray(posts);

  // Determine how many posts to display
  const isDesktop = window.matchMedia("(min-width: 766px)").matches;
  const postCount = isDesktop ? 6 : 4;

  const displayedPosts = posts.slice(0, postCount);

  const html = displayedPosts.map(post => {
  // Truncate title if longer than 70 characters
  const truncatedTitle = post.title.length > 46
 ? post.title.substring(0, 46) + '...' 
    : post.title;

  return `
    <div class="related-posts">
      <img src="${post.image}" alt="${truncatedTitle}" />
      <h1 class="rp-tittle">
        <a href="${post.link}">${truncatedTitle}</a>
      </h1>
      <hr class="rp-hr" />
      <div class="rp-meta">
        <span>By:</span> <span>${blogName}</span>
      </div>
    </div>
  `;
}).join('');

  container.innerHTML = html;
}

function fetchRelatedPosts() {
  const container = document.querySelector('.related-posts-containers');
  if (!container) return;

  // Avoid refetching if already fetched
  if (container.dataset.loaded === 'true') return;

  fetch('/feeds/posts/default?alt=json&max-results=50')
    .then(res => res.json())
    .then(data => {
      renderRelatedPosts(container, data);
      container.dataset.loaded = 'true'; // Mark as loaded
    })
    .catch(err => console.error('Error fetching related posts:', err));
}

window.addEventListener('DOMContentLoaded', fetchRelatedPosts);

//

//
function getReadingTime(text) {
  const wordsPerMinute = 200;
  const wordCount = text.trim().split(/\s+/).length;
  return Math.ceil(wordCount / wordsPerMinute); // in minutes
}

function getContentHash(content) {
  let hash = 0;
  for (let i = 0; i < content.length; i++) {
    const char = content.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash |= 0;
  }
  return hash;
}

// Store session start time per post
const postSessionStartTimes = {};

function initPostTimers() {
  const posts = document.querySelectorAll('.Post-Container');

  const now = new Date();

  posts.forEach(post => {
    const content = post.querySelector('.post-body').textContent;
    const postId = getContentHash(content);

    // Only initialize if not already tracking
    if (!(postId in postSessionStartTimes)) {
      postSessionStartTimes[postId] = now;
    }
  });
}

function updateReadingProgress() {
  const now = new Date();

  document.querySelectorAll('.Post-Container').forEach(post => {
    const content = post.querySelector('.post-body').textContent;
    const metaTime = post.querySelector('.meta-time');
    const postId = getContentHash(content);

    const startTime = postSessionStartTimes[postId];
    const secondsElapsed = Math.floor((now - startTime) / 1000);
    const minutesRead = Math.floor(secondsElapsed / 60);
    const secondsRead = secondsElapsed % 60;

    const totalReadingTime = getReadingTime(content); // in minutes

    if (secondsElapsed < 10) {
      metaTime.textContent = "Just Now";
    } else {
      metaTime.textContent = `${minutesRead} min ${secondsRead} sec read of ${totalReadingTime} min`;
    }
  });
}

// Initialize when script loads
initPostTimers();

// Start updating every second
updateReadingProgress();
setInterval(updateReadingProgress, 1000);
//

const audio = document.getElementById("bhaktamberAudio");
const icon = document.getElementById("iconState");
const buttonText = document.getElementById("buttonText");

function toggleBhaktamber() {
  if (audio.paused) {
    audio.play().then(() => {
      icon.className = "fas fa-pause-circle";
      buttonText.textContent = " Navkar Mantra";
    }).catch(err => {
      alert("Tap to allow audio.");
    });
  } else {
    audio.pause();
    icon.className = "fas fa-headphones";
    buttonText.textContent = "Navkar Mantra";
  }
}

audio.addEventListener("ended", () => {
  icon.className = "fas fa-headphones";
  buttonText.textContent = " Navkar Mantra";
});
